mapper.select:
SELECT COUNT (*) FROM ( SELECT r.*, m.* FROM review r JOIN members m ON r.user_id = m.user_id
WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'
)

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT r.*, m.user_name, m.user_image FROM review r JOIN members m ON r.user_id = m.user_id

			WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

		ORDER BY rv_id DESC
	) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}
