mapper.select:
SELECT * FROM ( SELECT g.ga_ID, g.ga_TITLE, g.USER_ID, g.ga_sdate, g.ga_edate, g.ga_content, g.ga_visitcount, g.ga_bmcount, m.USER_IMAGE, m.USER_NAME, ROW_NUMBER() OVER (ORDER BY g.ga_id DESC) AS rNum FROM gallery g JOIN members m ON g.user_id = m.user_id
WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'
) WHERE rNum>=#{start} AND rNum<=#{end}

SELECT *
	FROM (
	    SELECT c.cm_id, c.cm_content, c.cm_postdate, c.rv_id, c.mt_id, c.ga_id, c.user_id,
	           m.user_image, m.user_name,
	           ROW_NUMBER() OVER (ORDER BY c.cm_postdate DESC) AS rNum
	    FROM comments c
	    JOIN members m ON c.user_id = m.user_id
	    WHERE c.ga_id=#{ga_id}
	)
	WHERE rNum>=#{start} AND rNum<=#{end}

mapper.update:
UPDATE comments SET
	cm_content=#{cm_content}
WHERE cm_id=#{cm_id}
