mapper.select:
SELECT COUNT (*) FROM ( SELECT r.*, m.* FROM review r JOIN members m ON r.user_id = m.user_id
WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'
)

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT r.*, m.user_name, m.user_image FROM review r JOIN members m ON r.user_id = m.user_id
		
			WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'
		
		ORDER BY rv_id DESC
	) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}

SELECT * FROM (
	SELECT r.*, m.user_name, m.user_image FROM review r JOIN members m ON r.user_id = m.user_id
)
WHERE rv_id=#{rv_id}

SELECT *
	FROM (
	    SELECT rc.rvc_id, rc.ex_seq, rv.user_id, rv.rvc_content, rv.rvc_postdate, m.user_image, m.user_name, ROW_NUMBER() 
	    OVER (ORDER BY rv.rvc_postdate DESC) AS rNum
	    FROM review_comment rc
	    JOIN members m ON rc.user_id = m.user_id
	    WHERE rv_id=#{rv_id}
	)
	WHERE rNum>=#{start} AND rNum<=#{end}

mapper.update:
UPDATE review_comment SET
rvc_content=#{rvc_content}
WHERE rvc_id=#{rvc_id}
